import { useState, useEffect } from "react";

import useStats from "../utils/useStats";

export default function ConfirmedDetail({ url }) {
    const { stats, loading, error } = useStats(url);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error...</p>;

    // console.log(stats);

    return (
        <div>
            <h3>Confirmed</h3>
            <table>
                <thead>
                    <tr>
                        <td>Province</td>
                        <td>Confirmed</td>
                        <td>Recovered</td>
                        <td>Deaths</td>
                        <td>Active</td>
                    </tr>
                </thead>
                {stats &&
                    stats.map((province, i) => {
                        return (
                            <tbody key={i}>
                                <tr>
                                    <td>{province.provinceState || null}</td>
                                    <td>{province.confirmed}</td>
                                    <td>{province.recovered}</td>
                                    <td>{province.deaths}</td>
                                    <td>{province.active}</td>
                                </tr>
                            </tbody>
                        );
                    })}
            </table>
        </div>
    );
}
