import useStats from "../utils/useStats";
import styled from "styled-components";
import * as d3 from "d3";
import React from "react";

const StatGrid = styled.div`
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 1rem;
`;

const StatBlock = styled.div`
    background: grey;
    font-size: 2rem;
    background: #f2f2f2;
    border-radius: 2rem;
    display: grid;
    align-items: center;
    justify-items: center;
    text-align: center;
`;

export default function Stats({ url }) {
    const { stats, loading, error } = useStats(url);
    const myRef = React.createRef();
    // console.log(url, stats);

    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error...</p>;

    React.useEffect(() => {
        let accessToRef = d3.select(myRef.current);
        accessToRef.style("background-color", "red");
    }, []);

    const viewStats = stats ? (
        // <StatGrid>
        //     <StatBlock>
        //         <h3>Confirmed: </h3>
        //         <span>
        //             {stats.confirmed ? stats.confirmed.value : "Not Found Yet"}
        //         </span>
        //     </StatBlock>
        //     <StatBlock>
        //         <h3>Deaths: </h3>
        //         <span>
        //             {stats.deaths ? stats.deaths.value : "Not Found Yet"}
        //         </span>
        //     </StatBlock>
        //     <StatBlock>
        //         <h3>Recovered: </h3>
        //         <span>
        //             {stats.recovered ? stats.recovered.value : "Not Found Yet"}
        //         </span>
        //     </StatBlock>
        // </StatGrid>
        <div>
            <div>
                <h3>Confirmed: </h3>
                <span>
                    {stats.confirmed ? stats.confirmed.value : "Not Found Yet"}
                </span>
            </div>
            <div>
                <h3>Deaths: </h3>
                <span>
                    {stats.deaths ? stats.deaths.value : "Not Found Yet"}
                </span>
            </div>
            <div>
                <h3>Recovered: </h3>
                <span>
                    {stats.recovered ? stats.recovered.value : "Not Found Yet"}
                </span>
            </div>
        </div>
    ) : null;

    return viewStats;
}
